import React, { useState } from "react";
import "../styles.css";
import API from "../api"; // axios instance with your deployed baseURL

function Register() {
  const [formData, setFormData] = useState({ name: "", email: "", password: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMessage("");

    try {
      // üëá use your deployed backend via API.post()
      const res = await API.post("/api/auth/register", formData);

      setMessage("‚úÖ Registered successfully! You can now log in.");
    } catch (error) {
      console.error("Register error:", error);
      if (error.response && error.response.data?.message) {
        setMessage(`‚ùå ${error.response.data.message}`);
      } else {
        setMessage("‚ùå Error connecting to server.");
      }
    }
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="Name"
          onChange={handleChange}
          required
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          onChange={handleChange}
          required
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          onChange={handleChange}
          required
        />
        <button type="submit">Register</button>
      </form>
      <p>{message}</p>
    </div>
  );
}

export default Register;
